        # Find the indicator matching the workflow_name
        indicator = next(
            (ind for ind in indicators if ind.get("workflow") == workflow_name), None
        )
        if indicator is None:
            logger.warning(
                f"No indicator found for workflow '{workflow_name}'. Returning empty update."
            )
            return {}
        indicator_id = indicator.get("id")
